package com.bit;

import java.awt.Button;
import java.awt.Frame;
import java.awt.Panel;
import java.awt.event.MouseEvent;
import java.awt.event.MouseListener;

public class Ex08 extends Frame implements MouseListener{
	
	public Ex08() {
		
		Panel p = new Panel();
		Button btn = new Button("악악");
		
		btn.addMouseListener(this);
		
		p.add(btn);
		add(p);
		setBounds(600, 100, 300, 300);
		setVisible(true);
	}
	
	public static void main(String[] args) {
		new Ex08();
	}

	
	@Override
	public void mouseClicked(MouseEvent e) {
		// TODO Auto-generated method stub

		System.out.println("클릭");
		
	}

	@Override
	public void mousePressed(MouseEvent e) {
		// TODO Auto-generated method stub.
		// 기준 = 이벤트 객체
		int x = e.getX();
		int y = e.getY();
		System.out.println("누름 x:"+x+" y:"+y);
	}

	@Override
	public void mouseReleased(MouseEvent e) {
		// TODO Auto-generated method stub
		System.out.println("[눌렸었는데]올라옴");
	}

	@Override
	public void mouseEntered(MouseEvent e) {
		// TODO Auto-generated method stub
		System.out.println("[컴포넌트 위로] 올라옴");
	}

	@Override
	public void mouseExited(MouseEvent e) {
		// TODO Auto-generated method stub
		System.out.println("[컴포넌트에서] 빠져나감");
	}
}
